package com.feuji.commonmodel;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import java.util.Objects;import java.util.Set;@Entity@Table(name = "question_table")public class Question {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String content;    @Column(name = "option_A")    private String optionA;    @Column(name = "option_B")    private String optionB;    @Column(name = "option_C")    private String optionC;    @Column(name = "option_D")    private String optionD;    private String answer;    @Column(name = "q_type")    private String qtype;    @JsonIgnore    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name = "s_Id", nullable = false)    private Subject subject;    @JsonIgnore    @ManyToMany(cascade = CascadeType.ALL, mappedBy = "questions")    private Set<CreatePaper> exams;    public Question(String content, String option_A, String option_B, String option_C, String option_D, String answer, String q_type, Subject subject) {        this.content = content;        this.optionA = option_A;        this.optionB = option_B;        this.optionC = option_C;        this.optionD = option_D;        this.answer = answer;        this.qtype = q_type;        this.subject = subject;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }    public String getOption_A() {        return optionA;    }    public void setOption_A(String option_A) {        this.optionA = option_A;    }    public String getOption_B() {        return optionB;    }    public void setOption_B(String option_B) {        this.optionB = option_B;    }    public String getOption_C() {        return optionC;    }    public void setOption_C(String option_C) {        this.optionC = option_C;    }    public String getOption_D() {        return optionD;    }    public void setOption_D(String option_D) {        this.optionD = option_D;    }    public String getAnswer() {        return answer;    }    public void setAnswer(String answer) {        this.answer = answer;    }    public String getQ_type() {        return qtype;    }    public void setQ_type(String q_type) {        this.qtype = q_type;    }    public Subject getSubject() {        return subject;    }    public void setSubject(Subject subject) {        this.subject = subject;    }    public Question() {    }    @Override    public boolean equals(Object o) {        if (this == o) {            return true;        }        if (o == null || getClass() != o.getClass()) {            return false;        }        Question question = (Question) o;        return Objects.equals(id, question.id) && Objects.equals(content, question.content) && Objects.equals(optionA, question.optionA) && Objects.equals(optionB, question.optionB) && Objects.equals(optionC, question.optionC) && Objects.equals(optionD, question.optionD) && Objects.equals(answer, question.answer) && Objects.equals(qtype, question.qtype) && Objects.equals(subject, question.subject) && Objects.equals(exams, question.exams);    }    @Override    public int hashCode() {        return Objects.hash(id, content, optionA, optionB, optionC, optionD, answer, qtype, subject, exams);    }}